(function (global, factory) {
  if (typeof define === "function" && define.amd) {
    define(["oasis-engine", "@oasis-engine/controls"], factory);
  } else if (typeof exports !== "undefined") {
    factory(require("oasis-engine"), require("@oasis-engine/controls"));
  } else {
    var mod = {
      exports: {}
    };
    factory(global.oasisEngine, global["@oasisEngine/controls"]);
    global.index = mod.exports;
  }
})(typeof globalThis !== "undefined" ? globalThis : typeof self !== "undefined" ? self : this, function (_oasisEngine, _controls) {
  "use strict";

  var engine = new _oasisEngine.WebGLEngine("canvas");
  engine.canvas.resizeByClientSize();
  var scene = engine.sceneManager.activeScene;
  var rootNode = scene.createRootEntity("root"); // 在场景中创建相机节点、配置位置和目标方向

  var cameraNode = rootNode.createChild("camera_node");
  cameraNode.transform.setPosition(0, 0, 20);
  var camera = cameraNode.addComponent(_oasisEngine.Camera);
  camera.farClipPlane = 2000;
  var controler = cameraNode.addComponent(_controls.FreeControl);
  controler.movementSpeed = 100;
  controler.rotateSpeed = 1;

  var cuboid = _oasisEngine.PrimitiveMesh.createCuboid(engine, 50, 50, 50);

  var material = new _oasisEngine.BlinnPhongMaterial(engine);
  material.emissiveColor = new _oasisEngine.Color(0.5, 0.6, 0.6, 1);

  var groundGeometry = _oasisEngine.PrimitiveMesh.createPlane(engine, 2000, 2000, 100, 100);

  groundGeometry.subMesh.topology = _oasisEngine.MeshTopology.LineStrip;
  var groundMaterial = new _oasisEngine.BlinnPhongMaterial(engine);
  groundMaterial.emissiveColor = new _oasisEngine.Color(1, 1, 1, 1); // create meshes in scene

  for (var i = 0; i < 100; i++) {
    var cube = rootNode.createChild("cube");
    cube.transform.setPosition(Math.random() * 2000 - 1000, Math.random() * 200, Math.random() * 2000 - 1000);
    var cubeRenderer = cube.addComponent(_oasisEngine.MeshRenderer);
    cubeRenderer.mesh = cuboid;
    cubeRenderer.setMaterial(material);
  } // Ground


  var ground = rootNode.createChild("ground");
  ground.transform.setPosition(0, -25, 0);
  ground.transform.rotate(-90, 0, 0);
  var groundRender = ground.addComponent(_oasisEngine.MeshRenderer);
  groundRender.mesh = groundGeometry;
  groundRender.setMaterial(groundMaterial); // Run engine

  engine.run();
});
