})(typeof globalThis !== "undefined" ? globalThis : typeof self !== "undefined" ? self : this, function (_spine, _oasisEngine) {
  "use strict";

  /**
   * @title Spine Animation
   * @category 2D
   */
  _oasisEngine.Logger.enable();

  var engine = new _oasisEngine.WebGLEngine("canvas");
  engine.canvas.resizeByClientSize();
  var scene = engine.sceneManager.activeScene;
  var rootEntity = scene.createRootEntity(); // camera

  var cameraEntity = rootEntity.createChild("camera_node");
  var camera = cameraEntity.addComponent(_oasisEngine.Camera);
  cameraEntity.transform.position = new _oasisEngine.Vector3(0, 0, 70);
  engine.resourceManager.load({
    url: "https://sbfkcel.github.io/pixi-spine-debug/assets/spine/spineboy-pro.json",
    type: "spine"
  }).then(function (spineEntity) {
    spineEntity.transform.setPosition(0, -12, 0);
    rootEntity.addChild(spineEntity);
    var spineAnimation = spineEntity.getComponent(_spine.SpineAnimation);
    spineAnimation.state.setAnimation(0, "walk", true);
    spineAnimation.skeleton.scaleX = 0.05;
    spineAnimation.skeleton.scaleY = 0.05;
  });
  engine.run();
});
